import './App.css';
import Header from './componets/Header/Header';
import Formulario from './componets/Formulario/Form';
import Miorg from './componets/MiOrg';
import { useState } from 'react';
import Equipo from './componets/Equipo';
import Footer from './componets/Footer';


function App() {

  const [mostrarFormulario,actualizarMostrar] = useState(false);
  const [colaboradores,actualizarColaboradores] = useState([{
    team: "Programación",
    foto: "https://github.com/SebastianGz301.png",
    nombre: "Sebastian Gomez",
    puesto: "Desarrollador Junior"
  },
  {
    team: "Front End",
    foto: "https://github.com/harlandlohora.png",
    nombre: "Harland Lohora",
    puesto: "Instructor"
  },
  {
    team: "Front End",
    foto: "https://github.com/christianpva.png",
    nombre: "Christian Velasco",
    puesto: "Head de Alura e Instructor"
  },
  {
    team: "Data Science",
    foto: "https://github.com/JoseDarioGonzalezCha.png",
    nombre: "Jose Gonzalez",
    puesto: "Dev. FullStack"
  },
  {
    team: "Data Science",
    foto: "https://github.com/christianpva.png",
    nombre: "Christian Velasco",
    puesto: "Head de Alura e Instructor"
  },
  {
    team: "Data Science",
    foto: "https://github.com/genesysR-dev.png",
    nombre: "Genesys Rondón",
    puesto: "Desarrolladora de software e instructora"
  },
  {
    team: "Devops",
    foto: "https://github.com/christianpva.png",
    nombre: "Christian Velasco",
    puesto: "Head de Alura e Instructor"

  },
  {
    team: "Devops",
    foto: "https://github.com/harlandlohora.png",
    nombre: "Harland Lohora",
    puesto: "Instructor"
  },
  {
    team: "Devops",
    foto: "https://github.com/genesysR-dev.png",
    nombre: "Genesys Rondón",
    puesto: "Desarrolladora de software e instructora"
  },
  {
    team: "Devops",
    foto: "https://github.com/JeanmarieAluraLatam.png",
    nombre: "Jeanmarie Quijada",
    puesto: "Instructora en Alura Latam"
  }

]);


  //Equipos


  //Ternario --> condicion ? seMuestra : noSeMuestra
  //Condicion && seMuestra

  const cambiarMostrar = () =>{
    actualizarMostrar(!mostrarFormulario)
  }

  //Registrar Colaborador
  const registrarColaborador = (colaborador) => {
    console.log("Nuevo Colaborador:", colaborador);
    //Spread Operator
    actualizarColaboradores([...colaboradores, colaborador])
  }

  //Eliminar Colaborador
  const eliminarColaborador = (colaborador) => {
    console.log("Colaborador Eliminado:", colaborador);
  }

  //Actualizar Color
  const actualizarColor = (color, titulo) => {
    console.log("Nuevo Color:", color, titulo);
  }



  return (
    <div>
      <Header/>
      {mostrarFormulario && <Formulario
          equipos={equipos.map((equipo) => equipo.titulo)}
          registrarColaborador={registrarColaborador}
          />
        }


      <Miorg cambiarMostrar={cambiarMostrar}/> 

      { 
        equipos.map( (team) =><Equipo
        datos={team} 
        key={team.titulo}
        colaboradores={colaboradores.filter(colaborador => colaborador.team === team.titulo)}
        eliminarColaborador={eliminarColaborador}
        actualizarColor={actualizarColor}
        />
      )
      }
    <Footer/>
    </div>
  );
}

export default App;
